import React from 'react'
import { Link } from 'react-router-dom'
import Grid from '@material-ui/core/Grid'
import Grouptile from '../Grouptile'
import Menu from '@material-ui/core/Menu'
import MenuItem from '@material-ui/core/MenuItem'
import IconButton from '@material-ui/core/IconButton'
import Button from '@material-ui/core/Button'
import ButtonBase from '@material-ui/core/ButtonBase'
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import AddCircleOutlineIcon from '@material-ui/icons/AddCircleOutline';
import group1 from './static/group1.jpg'
import group2 from './static/group2.jpg'
import group3 from './static/group3.jpeg'
import './styles.css'
import CardMedia from '@material-ui/core/CardMedia'


class Grouplist extends React.Component {

    state = {
        anchorEl: null,
        joinGroupModal: false,
        createGroupModal: false,
        submitDisabled: true,
        code: null,
        groups: [
            {
                link: '/Group',
                image: group1,
                Groupname: "Group 1",
                Groupdescription: "This group is where the current user is joined as a user"
            },
            {
                link: '/Group',
                image: group2,
                Groupname: "Group 2",
                Groupdescription: "This group is where the current user is joined as a admin"
            }
        ],
        image: null
    }

    handleClose = () => {
        this.setState({
            anchorEl: null
        })
    }

    handleMenu = (event) => {
        this.setState({
            anchorEl: event.currentTarget
        })
    }

    handleGroupOpen = (option) => {
        this.setState({
            [option]: true
        })
    }

    handleGroupClose = (option) => {
        this.handleClose()
        this.setState({
            [option]: false
        })
    }

    handleInputChange = (event) => {
        const bool = event.target.value === "";
        this.setState({
            submitDisabled: bool,
            code: event.target.value
        })
    }

    joinGroup = () => {
        if (this.state.code === 'aaaa') {
            const newGroups = this.state.groups.concat({
                link: '/Group',
                image: group3,
                Groupname: `Group ${this.state.groups.length + 1}`,
                Groupdescription: "This is a group you just added"
            })
            this.setState({
                groups: newGroups,
                submitDisabled: true,
                code: null
            })
        }
        this.handleClose()
        this.handleGroupClose('joinGroupModal')
    }

    handleUpload = (event) => {
        console.log(event.target.files)
        this.setState({
            image: event.target.files[0]
        })
    }

    render() {
        return (
            <div className="Gridwrapper">
                <Grid
                    container
                    direction="row"
                    justify="space-evenly"
                    alignItems="center"
                    className="Groupgrid"
                    id="Groupgrid"
                >
                    {(this.state.groups || []).map(group => (
                        <Grouptile
                            link={group.link}
                            image={group.image}
                            Groupname={group.Groupname}
                            Groupdescription={group.Groupdescription}
                        />
                    ))}
                    <div>
                        <IconButton
                            onClick={this.handleMenu}
                            className="addGroup"
                        >
                            <AddCircleOutlineIcon className="addGroup"/>
                        </IconButton>
                        <Menu
                            anchorEl={this.state.anchorEl}
                            keepMounted
                            transformOrigin={{
                                vertical: 'bottom',
                                horizontal: 'right',
                            }}
                            open={Boolean(this.state.anchorEl)}
                            onClose={this.handleClose}
                            className="Addmenu"
                            PaperProps={{
                                style: {
                                    backgroundColor: "#b95c0d"
                                }
                            }}
                        >
                            <MenuItem className="Addmenuitem" onClick={() => this.handleGroupOpen('createGroupModal')}>Create Group</MenuItem>
                            <MenuItem className="Addmenuitem" onClick={() => this.handleGroupOpen('joinGroupModal')}>Join Group</MenuItem>
                        </Menu>
                    </div>
                </Grid>
                <Dialog open={this.state.createGroupModal} onClose={() => this.handleGroupClose('createGroupModal')} aria-labelledby="form-dialog-title">
                    <DialogTitle id="form-dialog-title">Create a New Group</DialogTitle>
                    <DialogContent>
                    <DialogContentText>
                        Please fill in the form to create a group
                    </DialogContentText>
                    <TextField
                        autoFocus
                        margin="dense"
                        id="name"
                        label="Group Name"
                        type="email"
                        fullWidth
                    />
                    <TextField
                        autoFocus
                        margin="dense"
                        id="standard-textarea"
                        label="Group Description"
                        type="email"
                        multiline
                        fullWidth
                    />
                    {this.state.image ? <CardMedia image={null}></CardMedia> : <DialogContentText>Import an image to continue</DialogContentText>}
                    <Button onClick={() => document.getElementById('fileInput').click()}>
                        Import Group image
                    </Button>
                    </DialogContent>
                    <DialogActions>
                    <Button onClick={() => this.handleGroupClose('createGroupModal')} color="primary">
                        Cancel
                    </Button>
                    <Button onClick={() => this.handleGroupClose('createGroupModal')} color="primary">
                        Create
                    </Button>
                    </DialogActions>
                </Dialog>

                <input id="fileInput" type="file" accept="image/*" style={{display: null}} onChange={this.handleUpload}/>

                <Dialog open={this.state.joinGroupModal} onClose={() => this.handleGroupClose('joinGroupModal')} aria-labelledby="form-dialog-title">
                    <DialogTitle id="form-dialog-title">Join a New Group</DialogTitle>
                    <DialogContent>
                    <DialogContentText>
                        To join a new group, please enter the code that was given to you by a current member of the group.
                    </DialogContentText>
                    <TextField
                        autoFocus
                        margin="dense"
                        id="name"
                        label="Group Code"
                        onChange={this.handleInputChange}
                    />
                    </DialogContent>
                    <DialogActions>
                    <Button onClick={() => this.handleGroupClose('joinGroupModal')} color="primary">
                        Cancel
                    </Button>
                    <Button onClick={() => this.joinGroup()} type="submit" disabled={this.state.submitDisabled} color="primary">
                        Join
                    </Button>
                    </DialogActions>
                </Dialog>
            </div>
        );
    }
}

export default Grouplist;
