import React from 'react'
import Grid from '@material-ui/core/Grid'
import Grouptile from '../Grouptile'
import Menu from '@material-ui/core/Menu'
import MenuItem from '@material-ui/core/MenuItem'
import IconButton from '@material-ui/core/IconButton'
import AddCircleOutlineIcon from '@material-ui/icons/AddCircleOutline';
import './styles.css'


class Grouplist extends React.Component {

    state = {
        anchorEl: null
    }

    handleClose = () => {
        this.setState({
            anchorEl: null
        })
    }

    handleMenu = (event) => {
        this.setState({
            anchorEl: event.currentTarget
        })
    }

    render() {
        return (
            <Grid
                container
                direction="row"
                justify="space-evenly"
                alignItems="center"
                className="Groupgrid"
            >
                <Grouptile
                    Groupname={"Group 1"}
                    Groupdescription={"This group is where the current user is joined as a user"}
                />
                <Grouptile
                    Groupname={"Group 2"}
                    Groupdescription={"This group is where the current user is joined as an admin"}
                />
                <div>
                    <IconButton
                        onClick={this.handleMenu}
                        className="addGroup"
                    >
                        <AddCircleOutlineIcon className="addGroup"/>
                    </IconButton>
                    <Menu
                        anchorEl={this.state.anchorEl}
                        keepMounted
                        transformOrigin={{
                            vertical: 'bottom',
                            horizontal: 'right',
                        }}
                        open={Boolean(this.state.anchorEl)}
                        onClose={this.handleClose}
                        className="Addmenu"
                    >
                    <MenuItem className="Addmenuitem" onClick={this.handleClose}>Create Group</MenuItem>
                    <MenuItem className="Addmenuitem" onClick={this.handleClose}>Join Group</MenuItem>
                </Menu>
                </div>
            </Grid>
        );
    }
}

export default Grouplist;
