import React from 'react'
import { Link } from 'react-router-dom'
import Grid from '@material-ui/core/Grid'
import Grouptile from '../Grouptile'
import Menu from '@material-ui/core/Menu'
import MenuItem from '@material-ui/core/MenuItem'
import IconButton from '@material-ui/core/IconButton'
import Button from '@material-ui/core/Button'
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import AddCircleOutlineIcon from '@material-ui/icons/AddCircleOutline';
import group1 from './static/group1.jpg'
import group2 from './static/group2.jpg'
import './styles.css'


class Grouplist extends React.Component {

    state = {
        anchorEl: null,
        joinGroupModal: false,
        createGroupModal: false
    }

    handleClose = () => {
        this.setState({
            anchorEl: null
        })
    }

    handleMenu = (event) => {
        this.setState({
            anchorEl: event.currentTarget
        })
    }

    handleOpen = (option) => {
        this.setState({
            [option]: true
        })
    }

    render() {
        return (
            <div className="Gridwrapper">
                <Grid
                    container
                    direction="row"
                    justify="space-evenly"
                    alignItems="center"
                    className="Groupgrid"
                >
                    <Grouptile
                        link={'/Group'}
                        image={group1}
                        Groupname={"Group 1"}
                        Groupdescription={"This group is where the current user is joined as a user"}
                    />
                    <Grouptile
                        link={'/Group'}
                        image={group2}
                        Groupname={"Group 2"}
                        Groupdescription={"This group is where the current user is joined as an admin"}
                    />
                    <div>
                        <IconButton
                            onClick={this.handleMenu}
                            className="addGroup"
                        >
                            <AddCircleOutlineIcon className="addGroup"/>
                        </IconButton>
                        <Menu
                            anchorEl={this.state.anchorEl}
                            keepMounted
                            transformOrigin={{
                                vertical: 'bottom',
                                horizontal: 'right',
                            }}
                            open={Boolean(this.state.anchorEl)}
                            onClose={this.handleClose}
                            className="Addmenu"
                            PaperProps={{
                                style: {
                                    backgroundColor: "#b95c0d"
                                }
                            }}
                        >
                            <MenuItem className="Addmenuitem" onClick={this.handleClose}>Create Group</MenuItem>
                            <MenuItem className="Addmenuitem" onClick={this.handleClose}>Join Group</MenuItem>
                        </Menu>
                    </div>
                </Grid>
                <Dialog open={this.state.createGroupModal} onClose={this.handleGroupClose} aria-labelledby="form-dialog-title">
                    <DialogTitle id="form-dialog-title">Subscribe</DialogTitle>
                    <DialogContent>
                    <DialogContentText>
                        To subscribe to this website, please enter your email address here. We will send updates
                        occasionally.
                    </DialogContentText>
                    <TextField
                        autoFocus
                        margin="dense"
                        id="name"
                        label="Email Address"
                        type="email"
                        fullWidth
                    />
                    </DialogContent>
                    <DialogActions>
                    <Button onClick={this.handleGroupClose} color="primary">
                        Cancel
                    </Button>
                    <Button onClick={this.handleGroupClose} color="primary">
                        Subscribe
                    </Button>
                    </DialogActions>
                </Dialog>
            </div>
        );
    }
}

export default Grouplist;

