import React from 'react'
import Memberlist from '../Memberlist'
import Diceroller from '../Diceroller'
import Chatbox from '../Chatbox'
import Menu from '@material-ui/core/Menu'
import MenuItem from '@material-ui/core/MenuItem'
import IconButton from '@material-ui/core/IconButton'
import Button from '@material-ui/core/Button'
import ButtonBase from '@material-ui/core/ButtonBase'
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import AddCircleOutlineIcon from '@material-ui/icons/AddCircleOutline';
import './style.css'

class Group extends React.Component {

    state = {
        inviteGroupModal: null
    }

    togglePop = () => {
        this.setState({
            inviteGroupModal: true
        });
    };

    handleClose = () => {
        this.setState({
            inviteGroupModal: null
        })
    }

    copyToClipboard = (event) => {
        this.textArea.select();
        document.execCommand('copy');
        event.target.focus();
        this.setState({ copySuccess: 'Copied!' });
    };

    render() {
        return (
            <div className="Group">
                <Memberlist
                    togglePop={this.togglePop}
                />
                <Chatbox/>
                <Diceroller/>
                <Dialog open={this.state.inviteGroupModal} onClose={() => this.handleClose()} aria-labelledby="form-dialog-title">
                    <DialogTitle id="form-dialog-title">Invite to Group</DialogTitle>
                    <DialogContent>
                    <DialogContentText>
                        Send this link to your friends to add them to the group!
                    </DialogContentText>
                    <TextField
                        autoFocus
                        margin="dense"
                        id="code"
                        label="Group Code"
                        ref={(textarea) => this.textArea = textarea}
                        value='bit.ly/dndgroup1234'
                    />
                    </DialogContent>
                    <DialogActions>
                    <Button onClick={() => this.handleClose()} color="primary">
                        Cancel
                    </Button>
                    <Button onClick={() => this.copyToClipboard()} type="submit" disabled={this.state.submitDisabled} color="primary">
                        Copy Link
                    </Button>
                    </DialogActions>
                </Dialog>
            </div>
        )
    }
}

export default Group;
